    private void ensinoRegularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ensinoRegularActionPerformed
        // TODO add your handling code here:
        try{
            EnsinoRegular ensnioregular = new EnsinoRegular();
            ensnioregular.setLocationRelativeTo(ensnioregular);
            ensnioregular.setVisible(rootPaneCheckingEnabled);
            dispose();            
        }catch (NumberFormatException e){
            System.out.println("INSIRA VALORES CORRETAMENTE!");
            JOptionPane.showMessageDialog(null, "INSIRA VALORES CORRETAMENTE!");
        }

    }//GEN-LAST:event_ensinoRegularActionPerformed

